import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import '../styling/WelcomePage.css';
import StarWars from '../images/star_wars.png';


const WelcomePage = () => {

    const isRoot = window.location.pathname == "/" ;


    if (isRoot){
        return (
            <div class="WelcomeMsg">
                <link rel="stylesheet" href="WelcomePagew.css"/>
                <link rel="preconnect" href="https://fonts.googleapis.com"/>
                <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin/>
                <link href="https://fonts.googleapis.com/css2?family=Poller+One&display=swap" rel="stylesheet"></link>

                <img id="swTitle" src={StarWars}></img>
                <p id="welText">Star Wars, an American epic space opera media franchise created by George Lucas.</p>
                <Button component={Link} to={`/HomePage`} id="welButton">View Films</Button>

            </div>
        )
    }

    else {
        return (
            <div></div>
        )
    }
}

export default WelcomePage;
