import React, { useState } from 'react';
import * as ReactBootStrap from 'react-bootstrap';
import Button from '@material-ui/core/Button';


const Characters = (props) => {

    const getCharacter = async () => {

        let arr = [];

        for (let i = 0; i < characters.length; i++){

            const character = await fetch(characters[i])
            const r = await character.json();
            arr.push(r.name)
        }
        setNames(arr);
        setLoading(false);
    }

    const [names, setNames ] = useState([])
    const [loading, setLoading] = useState(true);
    const characters = props.location.state.characters;
    getCharacter();


    if (!loading){

        return (
            <div>
                <div class="nameDisplay">
                    {names.map((n) => (
                        <div> {n} </div>
                    ))}
                </div>
                <div>
                <Button onClick={disappear} component={Link} to={`/HomePage`} id="welcomeButton">View Films</Button>
                </div>

            </div>
        )
    } else {
        return (
            <div>
                <link
                rel="stylesheet"
                href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css"
                integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC"
                crossorigin="anonymous"
                />
                <ReactBootStrap.Spinner animation="grow" />
                <p>Loading Characters</p>

            </div>
        )
    }
}

export default Characters;
