import React from 'react';
import '../styling/EpDisplay.css';
import StarWars from '../images/star_wars.png';

const Episodes = (props) => {

    function filterFilms( filmList ) {

        let filteredEpisode = new Array();

        for (const [film, value] of Object.entries(filmList)){

            if (value.director.toLowerCase() == "george lucas"){
                filteredEpisode.push(value)
            }
        }

        // Sort by episode_id
        filteredEpisode = filteredEpisode.sort(function(a, b){
            return a.episode_id - b.episode_id;
        })
        return filteredEpisode;
    } 

    let targetEpisodes = filterFilms(props.ep);
    console.log(targetEpisodes)

    return (
        <div class="epDisplay">
            <link rel="stylesheet" href="EpDisplay.css"/>
            <img id="swTitle" src={StarWars}></img>

            <div class="ep">
                {targetEpisodes.map( (movies) => (
                    <div> 
                        {/* {movies.title} {movies.episode_id} {movies.opening_crawl} */}
                    </div>

                ))}
            </div>

        </div>
    )
}

export default Episodes;
